document.addEventListener('DOMContentLoaded', _ => Array.from(document.forms).forEach(f => Array.from(f.elements).forEach(e => e.type && e.type.toUpperCase().startsWith('X') && (e.type = e.type.slice(1), console.log(`Changed input type from 'X${e.type}' to '${e.type}'`)))));
